    // Clean up the product name to make it URL-safe
    const formattedName = name.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, ''); 
    // concat the name and id
    const fullPath = `${formattedName}-${id}`;
    return `/${url}/${fullPath}`;
}




