import React from 'react';

const downloadFile = (fileId, fileName, fileExt) => {
    const fileUrl = `/download.php?id=${fileId}&filename=${fileName}&ext=${fileExt}`;
    const link = document.createElement("a");
    link.href = fileUrl;
    link.setAttribute("download", `${fileName.replace(/ /g, "_")}.${fileExt}`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
};

// Generate **direct file URL** for preview
const getPreviewUrl = (fileId, fileName, fileExt) => {
    return `/download.php?id=${fileId}&filename=${fileName}&ext=${fileExt}`;
};

function DownloadItem({ 
    id,
    thumb,
    description,
    index_description,
    file_name,
    file_ext
}) {
    return (
        <div className="col-xl-4 col-md-6 col-12">
            <div className="single-news-card">
                <div
                    className="featured-thumb bg-cover"
                    style={{ backgroundImage: `url(${thumb})` }}
                />
                <div className="post-content">
                    <div className="post-meta d-flex">
                        <div className="post-author mr-2">
                            <i className="fal fa-user" />
                            <a href="#" onClick={(e) => e.preventDefault()}>{index_description}</a>
                        </div>
                    </div>
                    <h3>
                        {/* Open preview in new tab (direct file link) */}
                        <a 
                            href={getPreviewUrl(id, file_name, file_ext)}
                            target="_blank" 
                            rel="noopener noreferrer"
                        >
                            {description}
                        </a>
                    </h3>
                    {/* Download button */}
                    <button 
                        className="btn btn-primary cursor-pointer mt-3"
                        onClick={() => downloadFile(id, file_name, file_ext)}
                    >
                        Download Now
                    </button>
                </div>
            </div>
        </div>
    );
}

export default DownloadItem;
