import React from 'react';
import { Link } from 'react-router-dom';

const downloadFile = async (fileUrl, fileName, fileExt) => {
    try {
        const response = await fetch(fileUrl);
        if (!response.ok) throw new Error("File not found");

        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);

        // Force file download like PHP
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", `${fileName.replace(/ /g, "_")}.${fileExt}`);
        document.body.appendChild(link);
        link.click();

        // Cleanup
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
    } catch (error) {
        console.error("Download failed:", error);
    }
};



function BlogThreeItem({ thumb, author, meta, title, postLink, category }) {
    return (
        <div className="col-xl-4 col-md-6 col-12">
            <div className="single-news-card">
                <div
                    className="featured-thumb bg-cover"
                    style={{ backgroundImage: `url(${thumb})` }}
                />
                <div className="post-content">
                    <div className="post-meta d-flex">
                        <div className="post-author mr-2">
                            <i className="fal fa-user" />
                            <Link to="/home-3">{author}</Link>
                        </div>
                        |
                        <div className="post-date ml-2">
                            <i className="fal fa-calendar-alt" />
                            <span>{meta}</span>
                        </div>
                    </div>
                    <h3>
                        <Link to={postLink}>{title}</Link>
                    </h3>
                    {
                        category === "Download" && (
                            <button 
                                className="btn btn-primary mt-3 cursor-pointer" 
                                onClick="/">
                                Download Now
                            </button>
                        )
                    }

                </div>
                {/* <Link to="/" className="post-cat">
                    {category}
                </Link> */}
            </div>
        </div>
    );
}

export default BlogThreeItem;
