export function concatUrlPath(url, name, id) {
    // Clean up the product name to make it URL-safe
    const formattedName = name.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, ''); 
    // concat the name and id
    const fullPath = `${formattedName}-${id}`;
    return `/${url}/${fullPath}`;
}




export function extractIdFromUrlPath(path) {
  
  // Match the pattern "/[category]/[name]-[id]" where category can contain hyphens
  const regex = /\/[a-z0-9-]+\/[a-z0-9-]+-(\d+)$/;
  const match = path.match(regex);
  if (match) {
      return match[1]; // Extract and return the ID
  }
  
  return null;
}
