import React from 'react';

const downloadFile = async (fileId, fileName, fileExt) => {
    try {
        const fileUrl = `/download.php?id=${fileId}&filename=${fileName}&ext=${fileExt}`; // Adjust based on backend

        const response = await fetch(fileUrl);
        if (!response.ok) throw new Error("File not found");

        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);

        // Force file download
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", `${fileName.replace(/ /g, "_")}.${fileExt}`);
        document.body.appendChild(link);
        link.click();

        // Cleanup
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
    } catch (error) {
        console.error("Download failed:", error);
    }
};

// Function to generate the file preview URL
const getPreviewUrl = (fileId) => {
    return `/uploads/${fileId}`; // Adjust based on actual file storage
};

function DownloadItem({ 
    id,
    thumb,
    description,
    index_description,
    file_name,
    file_ext
}) {
    return (
        <div className="col-xl-4 col-md-6 col-12">
            <div className="single-news-card">
                <div
                    className="featured-thumb bg-cover"
                    style={{ backgroundImage: `url(${thumb})` }}
                />
                <div className="post-content">
                    <div className="post-meta d-flex">
                        <div className="post-author mr-2">
                            <i className="fal fa-user" />
                            <a href="#" onClick={(e) => e.preventDefault()}>{index_description}</a>
                        </div>
                    </div>
                    <h3>
                        {/* Open preview in new tab */}
                        <a href={getPreviewUrl(id)} target="_blank" rel="noopener noreferrer">
                            {description}
                        </a>
                    </h3>
                    {/* Download button */}
                    <button 
                        className="btn btn-primary cursor-pointer mt-3"
                        onClick={() => downloadFile(id, file_name, file_ext)}
                    >
                        Download Now
                    </button>
                </div>
            </div>
        </div>
    );
}

export default DownloadItem;
