                            <span>{props?.heading1}</span>
                            <p>{props?.heading2}</p>
                            <h1>{props?.heading3}</h1>
                        </div>
                    </div>
                </div> */}
                {/* <div className="row" style={{marginTop:"20px",width:"100%"}}>
                    <img src={'https://smilesdahub.com/tf/apis/cat_images/banner_'+suCrypt(lastWord)+'.jpg'}  />
                </div> */}
