                            // defaultImg={process.env.REACT_APP_IMAGE_URL +'images/product_testfabrics.jpg'}
                            defaultImg={defaultImage}
                            icon={icon1}
                            index={index+1}
                            heading={item.category__Name}
                            btnText="View Subcategories"
                        />

                    ))
                    :<></>}
                </div>
            </div>
        </section>
    );
}

export default Services1;
